class Cycle {
    freq; // Frequency
    amp; // Amplitude (length)
    phase; // Phase (starting angle)
    constructor(_freq, _amp, _phase) {
        this.freq = _freq;
        this.amp = _amp;
        this.phase = _phase;
    }
}

class Complex {
    constructor(a, b) {
        this.re = a;
        this.im = b;
    }

    add(c) {
        this.re += c.re;
        this.im += c.im;
    }

    mult(c) {
        const re = this.re * c.re - this.im * c.im;
        const im = this.re * c.im + this.im * c.re;
        return new Complex(re, im);
    }
}

