function draw() {
    background(0);
    let v = drawCycles(cycles);
    path.unshift(world2screen(v));

    beginShape();
    noFill();
    for (let i = 0; i < path.length; i++) {
        vertex(path[i].x, path[i].y);
    }
    endShape();

    const dt = TWO_PI / cycles.length;
    t += dt;
    if (t > TWO_PI) {
        t = 0;
        path = [];
    }
}
